const fs = require('fs')
const validator = require('./index')
const options = {
  format: 'text',
  data: fs.readFileSync('test/data/invalid.html'),
  ignore: 'Error: Stray end tag “div”.'
}
validator(options)
  .then((data) => {
    console.log(data)
  })
  .catch((error) => {
    console.error(error)
  })
